import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import { useDispatch } from 'react-redux';
import { useParams } from 'react-router-dom';
import { fetchAsyncMovieOrShowDetail, getSelectedMovieOrShow } from '../../features/movies/movieSlice';

const MovieDetail = () => {
    const { imdbID } = useParams();
    const dispatch = useDispatch();
    const data = useSelector(getSelectedMovieOrShow); // fetching data from redux store
    // console.log(data);

    useEffect(() => {
      dispatch(fetchAsyncMovieOrShowDetail(imdbID));
    },[dispatch, imdbID])

    return(
        <div className='movie-section'>
           <div className="section-left">
               <div className="movie-title">{data.Title}</div>
               <div className="movie-rating">{data.Rating}</div>
           </div>
        </div>
  );
};

export default MovieDetail;
