import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

// its take two arguments identifier and callvbackfunction and one more is object
const fetchAsyncMovies = createAsyncThunk('movies/fetchAsyncMovies', async() => {
    const response = await movieApi.get(`?apikey=${APIkey}&s=${movieText}&type=movie`)
    .catch(err => console.log("Eror is: ",err));
    console.log("The respone from api", response)
    dispatch(addMovies(response.data))
})

const initialState = {
    movies:{},
}

const movieSlice = createSlice({
    name:"movies",
    initialState,
    reducers:{
        addMovies : (state, {payload}) => {
            state.movies = payload;
        },
    },
    
})

export const {addMovies} = movieSlice.actions;
export const getAllMovies = (state) => state.movies.movies
export default movieSlice.reducer;
